package com.example.echatmobile.systemimport android.os.Bundleclass BaseEvent<out T : BaseEventTypeInterface>(private val eventType: T) {    private var isTaken = false    fun get(): T? {        if (!isTaken) {            isTaken = true            return eventType        }        return null    }    fun getDespiteProcessing() = eventType}interface BaseEventTypeInterfaceclass ToastStringEvent(val text: String, val length: Int) : BaseEventTypeInterfaceclass ToastResourceEvent(val resource: Int, val length: Int) : BaseEventTypeInterfaceclass HideKeyboardEvent : BaseEventTypeInterfaceclass NavigateEvent(val action: Int, val navigationData: Bundle?) : BaseEventTypeInterface